public class Position {
	private int xPos;
	private int yPos;
	
	/**
	 * 
	 */
	public Position() {
		// TODO Auto-generated constructor stub
	}
	public Position(int xPos, int yPos) {
		// TODO Auto-generated constructor stub
		this.xPos = xPos;
		this.yPos = yPos;
	}
	public int getxPos() {
		return xPos;
	}

	public void setxPos(int xPos) {
		this.xPos = xPos;
	}

	public int getyPos() {
		return yPos;
	}

	public void setyPos(int yPos) {
		this.yPos = yPos;
	}
}
